// Pega todos os eventos retornados
const eventos = $input.all().map(item => item.json);

// Garante que o modelo receba uma lista limpa e simples
const listaEventos = eventos.map(e => ({
  summary: e.summary,
  start: e.start?.dateTime,
  end: e.end?.dateTime
}));

// Retorna tudo como um único item JSON
return [{
