let output = $input.first().json.text || '';
output = output
  .replace(/```json/i, '')
  .replace(/```/g, '')
  .trim();
const dados = JSON.parse(output);

function capitalizeEachWord(sentence) {
  return sentence.split(' ').map(word => {
    if (word.length === 0) { // Handle empty strings if any
      return '';
    }
    return word.charAt(0).toUpperCase() + word.slice(1);
  }).join(' ');
}
try {
  const dataInicial = new Date(dados.data).toISOString();

  let dataFinal = new Date(dados.data);
  dataFinal.setHours(dataFinal.getHours() + 1);


  const dataStart = new Date(dados.data);
  const formatador = new Intl.DateTimeFormat('pt-BR', {
    year: 'numeric',
    month: '2-digit',
    day: '2-digit',
    hour: '2-digit',
    minute: '2-digit',
    timeZone: 'America/Sao_Paulo'
  });
  const dataFormatada = formatador.format(dataStart).replace(',', '');

  const dataFormatadaMYSQL = new Date(dados.data).toISOString().replace('T', ' ').slice(0,19)

  return [{
    json: {
      nome: capitalizeEachWord(dados.nome) || $('Buscar Estado').first().json.nome,
      dataOk: dados.dataOk,
      data_google_calendar_start: dataInicial,
      data_google_calendar_end: dataFinal.toISOString(),
      data_a_exibir: dataFormatada,
      data_mysql: dataFormatadaMYSQL,
      telefone: $('Extrair Dados WhatsApp').first().json.to,
      to: $('Extrair Dados WhatsApp').first().json.telefone
    }
  }];
} catch (error) {
  return [{json: {
    dataOk: false,
    nome: capitalizeEachWord(dados.nome) || $('Buscar Estado').first().json.nome,
    telefone: $('Extrair Dados WhatsApp').first().json.to,
    to: $('Extrair Dados WhatsApp').first().json.telefone
  }}];
}

