let output = $input.first().json.text || '';
output = output
  .replace(/```json/i, '')
  .replace(/```/g, '')
  .trim();
const dados = JSON.parse(output);

function capitalizeEachWord(sentence) {
  return sentence.split(' ').map(word => {
    if (word.length === 0) { // Handle empty strings if any
      return '';
    }
    return word.charAt(0).toUpperCase() + word.slice(1);
  }).join(' ');
}

const dataInicial = new Date(dados.data).toISOString();

let dataFinal = new Date(dados.data);
dataFinal.setHours(dataFinal.getHours() + 1);


